    public function salvar_editar(){
        if(isset($_POST['id'])){
            $sql = "UPDATE `" . DB_PREFIX . "correios_offline5_base` SET inicio = '".(int)$_POST['cep_inicio']."', fim = '".(int)$_POST['cep_final']."', base_cep = '".(int)$_POST['cep_base']."' WHERE id = '".(int)$_POST['id']."' LIMIT 1";
