        }
        $this->response->redirect($this->url->link('module/correios_offline5', 'tab=base_ceps&token=' . $this->session->data['token'], 'SSL'));
    }
    
    public function remover(){
        if(isset($_GET['id'])){
            $sql = "DELETE FROM `" . DB_PREFIX . "correios_offline5_base` WHERE id = '".(int)$_GET['id']."' AND esedex = '0' LIMIT 1";
