		$data['footer'] = $this->load->controller('common/footer');
		$this->response->setOutput($this->load->view($tema, $data));
    }
    
    public function editar(){
        $data['nova'] = $this->url->link('module/correios_offline5/salvar_editar', 'token=' . $this->session->data['token'], 'SSL');
        $sql = "SELECT * FROM `" . DB_PREFIX . "correios_offline5_base` WHERE id = '".(int)$_GET['id']."' LIMIT 1";
